import { Component } from "@angular/core";

@Component ({
    selector: "bai1",
    templateUrl: "./bai1.component.html",
    styleUrls: ["./bai1.component.css"]
})

export class BaiTapComponent {
    so: number = 0;
    anh: string = "data:image/jpeg;base64,";

    CONG() {
        this.so = this.so + 1;
    }
    
    TRU() {
        this.so = this.so - 1;
    }

    MOUSEOVER(e: any) {
        this.anh = "data:image/jpeg;base64,";
        e.srcElement.src = this.anh;
    }
    
    MOUSEOUT(e: any) {
        this.anh = "data:image/jpeg;base64,";
        e.srcElement.src = this.anh;
    }
}
